class Solution {
public:
    int ans = 0;
    int dfs(int x,vector<vector<int>>& adj,vector<int>& cost)
    {
        if(adj[x].size()==0)
        return cost[x-1];
        int l = dfs(adj[x][0],adj,cost);
        int r = dfs(adj[x][1],adj,cost);
        if(abs(l-r)>0)
        ans+=max(l,r)-min(l,r);
        return cost[x-1]+max(l,r);
    }
    int minIncrements(int n, vector<int>& cost) {
        vector<vector<int>> adj(n+1);
        for(int i=1;i<=n;i++)
        {
            if(2*i<=n && 2*i+1<=n)
            {
                adj[i].push_back(2*i);
                adj[i].push_back(2*(i)+1);
            }
        }
        ans = 0;
        dfs(1,adj,cost);
        return ans;
    }
};
