var minIncrements = function (n, c) {
    let tot = 0;
    function dfs(i) {
        if (2 * i < n) {
            dfs(2 * i); // left child
            dfs(2 * i + 1);
            tot += Math.abs(c[2 * i - 1] - c[2 * i]); // diff of chldren
