# 3. Minimum number of moves in a grid
```
class Solution {
public:
    vector<int> I = {-1, 0, 1};
    vector<int> J = {1, 1, 1};
    int res = 0;
    int dfs(vector<vector<int>>& grid, vector<vector<int>>& dp, vector<vector<int>>& v, int i, int j){
        if(dp[i][j] != -1) return dp[i][j];
        v[i][j] = 1;
        cout<<grid[i][j]<<" ";
        for(int k = 0; k<3; ++k){
            int newI = i + I[k];
            int newJ = j + J[k];
            int ans = 0;
            if(newI >= 0 && newJ >=0 && newI < grid.size() && newJ <grid[0].size()){
                if(dp[newI][newJ] == -1 && !v[newI][newJ] && grid[i][j] < grid[newI][newJ]){
                    ans = 1+dfs(grid, dp, v, newI, newJ);
                }
                dp[i][j] = max(ans, dp[i][j]);
                res = max(res, dp[i][j]);
            }
        }
        return dp[i][j] == -1 ? 0:dp[i][j];
    }
    int maxMoves(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        vector<vector<int>> dp(n, vector<int>(m,-1));
        vector<vector<int>> v(n, vector<int>(m, 0));
        for(int i = 0; i<n; i++){
            for(int j = 0; j<1; ++j){
                if(dp[i][j] == -1)
                    dfs(grid, dp, v, i, j);
            }
        }
        int ans = res;
        res = 0;
        return ans;
    }
};
```
# 4. Count the Number of Complete Components
```
class Solution {
public:
    void dfs(vector<int> adj[], int i, vector<vector<int>>& m, vector<int>& v, vector<int>& res){
        if(!v[i]){
            res.push_back(i);
            v[i] = 1;
            for(int x: adj[i]) dfs(adj, x, m, v, res);
        }
    }
    int countCompleteComponents(int n, vector<vector<int>>& e) {
        vector<int> a[n];
        map<pair<int,int>, int> t;
        for(auto x: e){
            a[x[0]].push_back(x[1]);
            a[x[1]].push_back(x[0]);
            t[{x[0], x[1]}]++;
            t[{x[1], x[0]}]++;
        }
        vector<vector<int>> m;
        vector<int> v(n);
        for(int i = 0; i<n; ++i){
            vector<int> res;
            if(!v[i]){
                dfs(a, i, m, v, res);
                m.push_back(res);
            }
        }
        int cnt = m.size(), f = 0;
        for(auto x: m){
            for(int i = 0; i<x.size(); ++i){
                for(int j = i+1; j<x.size(); ++j){
                    if(!t[{x[i], x[j]}]){
                        cnt--;
                        f = 1;
                        break;
                    }
                }
                if(f){
                    f = 0;
                    break;
                }
            }
        }
        return cnt;
    }
};
```

