class Solution {
    private:
    bool solve(int val,vector<int>& derived,vector<int>&original ){
        original[0]=val;
         for(int i=0;i<derived.size();i++){
             if(i==derived.size()-1){
                 if((~val^original[0])==derived[i]){
                     original[i]=~val;
                 }
                 if((val^original[0])==derived[i]){
                     original[i]=val;
                 }
             } 
             else{
                 // original[i+1] = 0/1
                if((~val^original[i])==derived[i]){
                     original[i+1]=~val;
                 }
                if((val^original[i])==derived[i]){
                     original[i+1]=val;
                 }
             }
