         vector<int>dr(derived.size());

         for(int i=0;i<original.size();i++){
             if(i==derived.size()-1){
                dr[i]=original[i]^original[0];
             }
             else{
                 dr[i]=original[i]^original[i+1];
             }
         }

         return derived==dr;
    }
public:
    bool doesValidArrayExist(vector<int>& derived) {
        vector<int>original(derived.size());
        bool ans=solve(0,derived,original) or solve(1,derived,original);
        return ans;

    }
};
