public:

    bool isvalid(bool first,bool pre,int ind ,vector<int>&v)
    {
        if(ind==v.size()-1)
        {
            if(v[v.size()-1]==1)
                return first!=pre;
            return first==pre;
        }
        if(v[ind]==1)
        {
           if(pre)
               return isvalid(first,0,ind+1,v);
            return isvalid(first,1,ind+1,v);
        }
        if(pre)
            return isvalid(first,1,ind+1,v);
        return isvalid(first,0,ind+1,v);

    }
    bool doesValidArrayExist(vector<int>& derived) {
        return isvalid(0,0,0,derived)||isvalid(1,1,0,derived);
    }
};
