class Solution {
public:
    bool doesValidArrayExist(vector<int>& derived) {
        if(derived.size()==1)
        {
            if(derived[0]==0)
            {
                return true;
            }
            return false;
        }
        int n = derived.size();
        if(derived[0]==0)
        {
            int temp = 0;
            for(int i=1;i<derived.size()-1;i++)
            {
                temp = temp^derived[i];
            }

            if((temp^0)==derived[n-1])
            {
                return true;
            }

            temp = 1;
            for(int i=1;i<derived.size()-1;i++)
            {
