            ori1.push_back(1);
            ori1.push_back(0);
            ori2.push_back(0);
            ori2.push_back(1);
        }
        for(int i = 1; i < der.size() - 1; i++){
            if(der[i] == 0){
                if(ori1[i] == 0)
