class Solution {
    public boolean doesValidArrayExist(int[] derived) {
        return check(0, derived) || check(1, derived);
    }

    private boolean check(int start, int[] derived) {
        int n = derived.length;
        int curr = start;

        for (int i = 0; i < n - 1; i++) {
            curr ^= derived[i];
        }

        return (curr ^ start) == derived[n - 1];
    }
}
