class Solution {
public:
    bool firstCheck = true;
    bool secondCheck = true;
    void updateVariation(vector<vector<int>> & variation, int & target, int & index) {
        for (int i = 0; i < 2; i++) {
            if (i == 0 && !firstCheck || i == 1 && !secondCheck) {
                continue;
            }
            vector<int> & var = variation[i];
            if (var[index + 1] != -1 && var[index] ^ var[index + 1] != target) {
                i == 0 ? firstCheck = false : secondCheck = false;
            }
            if (target == 0) {
                var[index + 1] = var[index] == 0 ? 0 : 1;
            }
            else {
                var[index + 1] = var[index] == 0 ? 1 : 0;
            }
        }
    }

    bool doesValidArrayExist(vector<int>& derived) {
        int n = derived.size();
        if (n == 1 ) {
            return derived[0] == 0;
        }
        vector<vector<int>> variation(2, vector<int>(n, -1));
        if (derived[n - 1] == 1) {
            variation[0][0] = 1;
            variation[0][n - 1] = 0;
            variation[1][0] = 0;
            variation[1][n - 1] = 1;
        }
        else {
            variation[0][0] = 1;
            variation[0][n - 1] = 1;
            variation[1][0] = 0;
            variation[1][n - 1] = 0;
        }

        for (int i = 0; i < n - 1; i++) {
            updateVariation(variation, derived[i], i);
            if (!firstCheck && !secondCheck) {
                return false;
            }
        }

        return true;

    }
};
```

