    string removeTrailingZeros(string num) {
        bool seen = false;
        int i = num.size() - 1;

        while(!seen){
          if(!seen && num[i] == '0'){
            i--;
          }
          else if(!seen && num[i] != '0'){
            seen = true;
          }
        }

        return {num.begin(), num.begin() + (i + 1)};
    }
};
