public class Solution {
    public int makeTheIntegerZero(int num1, int num2) {
        if (num1 < num2) {
            return -1;
        }

        for (int i = 0; i <= 100; i++) {
            long diff = num1 - (long) num2 * i;
            int bbbiiiiittttsss = countBits(diff);
            if (bbbiiiiittttsss <= i && i <= diff) {
                return i;
            }
        }

        return -1;
    }

    private int countBits(long num) {
        int counter = 0;
        while (num > 0) {
            counter += num & 1;
            num >>= 1;
        }
        return counter;
    }
}
