class Solution {
public:
    /* rsingh2003 */

    int makeTheIntegerZero(int num1, int num2) {
        for(int i = 0; i <= 60; i++)
        {
            long long ab = num1*1ll - 1ll*i*num2;
            if(ab<0)
                break;
            int cnt = 0;
            long long bc = ab;
            while(ab != 0)
            {
                ab = ab&(ab-1ll);
                cnt++;
            }
            if(bc >= i && cnt != 0 && cnt <= i)
                return i;
        }
        return -1;
    }
};
```

