        if(num1<=num2) return -1;
        for(ll i=1; i<=(1e9); i++){
            long long num=((ll)num1-(i*1ll*num2));
            if(num<=0) return -1;
            long long co=0;
            long long dup=num;
            while(num>0){
                if(num&1) co++;
                num/=2;
            }
            if(co>i) continue;
            if(dup<i) continue;
            else return i;
        }
        return -1;
