class Solution {
public:
    #define ll long long
    int makeTheIntegerZero(int num1, int num2){
        int ans = -1;
        for(int i=1;i < 100;i++){
            ll temp = num1 - 1LL*i*num2;
            if(temp > 0){
                ll num = temp;
                int bit = 0;
                while(num){
                    if(num%2 == 1) bit += 1;
                    num /= 2;
                }
                if(bit <= i and i <= temp){
                    ans = i;
                    break;
                }
            }
        }
        return ans;
    }
};
```

