class Solution {
    public int makeTheIntegerZero(int num1, int num2) {
        long ans = -1;
        for(long i = 1 ; i <= 60 ; i++){
            long check = num1 - (i * num2);
            if(check <= 0) continue;
            int bits = setBits(check);
            if(bits > i) continue;
            if(check < i) continue;
            ans = i;
            break;
        }
        return (int) ans;
