class Solution {
public:
    int makeTheIntegerZero(int num1, int num2) {
        if(num1<num2)return -1;
        long long a = num2;
        int count=1;
        while(count<60){
            long long temp = num1-a;
            if(temp<0)break;
            long long t = 0;
            long long maxi=0;
            long long c = 0;
            while(temp){
                c++;
                if(temp&1){
                    t++; 
                    maxi+=c;
                }
                temp=temp>>1;
            }
            if((t==count)||((t<count)&&(maxi>=count)))return count;
            a+=num2;
            count++;
        }
        return -1;
    }
};
