class Solution {
public:
    int makeTheIntegerZero(int num1, int num2) {
        for (int k = 0; k <= 60; k++) {
            long long target = 1LL*num1 - 1LL* k * num2;
            if (target >= 0 && countBits(target) <= k && k <= target) {
                return k;
            }
        }
        return -1;
    }

private:
    int countBits(long long num) { 
        int count = 0; 
        while (num > 0) { 
            count += num & 1; 
            num >>= 1; 
        } 
        return count; 
    } 
};
