class Solution {
    public int makeTheIntegerZero(long num1, long num2) {
        int N = 0;
        while (num1 > num2) {
            num1 -= num2;
            N++;
            if (Long.bitCount(num1) <= N && num1 >= N) {
                return N;
            } 
        }
        return -1;
    }
}
