# Code
```
class Solution {
public:
    int makeTheIntegerZero(int num1, int num2) {
        int ans = 0;
        for (int i = 1; i <= 40; i++) {
            long long x = num1 - (long long) i * num2;
            if (x <= 0) break;
            if (i > x) continue;
            if (__builtin_popcountll(x) > i) continue;
            return i;
        }
        return -1;
    }
};
