class Solution {
    public int makeTheIntegerZero(int num1, int num2) {

        Queue<int[]> queue = new LinkedList<>();
        Set<Integer> visited = new HashSet<>();

        queue.offer(new int[] {0,0});
        visited.add(num2);
        int operations = 0;

        int [] p2 = new int[31];

        for(int i = 0; i < 31; i++){
            p2[i] = (int) Math.pow(2L, i);
        }

        while (!queue.isEmpty()) {
            int size = queue.size();

            for (int i = 0; i < size; i++) {
                int [] current = queue.poll();

                if (current[0] == num1) {
                    return current[1];
                }
                for (int j = 30; j >=0; j--) {
                    int next = current[0] + (p2[j] + num2);

                    if (num1 >= next && !visited.contains(next)) {
                        queue.offer(new int[]{next, current[1] + 1});
                        visited.add(next);
                    }
                }
            }
        }
        return -1;
    }
}
```

