        n = nums.size();
        vector<long long> temp;
        temp.reserve(n);
        for (int i = 0; i < n; i++) {
            long long mist = nums[i];
            while (i < n - 1 && ((nums[i] & 1) == (nums[i + 1] & 1))) {
                mist += nums[i + 1];
                i++;
            }
            temp.push_back(mist);
        }

        n = temp.size();
        curr.resize(2, 0);
        next.resize(2, 0);

        for(int i=n-1; i>=1; i--) {
            curr[0] = max(next[0], temp[i] + next[i&1] - (((i&1) == 0) ? 0 : x));
            curr[1] = max(next[1], temp[i] + next[i&1] - (((i&1) == 1) ? 0 : x));
            next = curr;
        }

        return temp[0] + curr[0];
    }
};

