class Solution {
public:

    #define ll long long

    long long maxScore(vector<int>& nums, int x) {
        int n=nums.size();
        vector<vector<ll int>> dp(n,vector<ll int>(2,0));

        int parity = nums[0] & 1;
        dp[0][parity] = nums[0];
        dp[0][parity^1] = nums[0]-x;

        ll int ans=dp[0][parity];

        for(int i=1;i<n;i++){
            parity = nums[i] & 1;

            // skip
            dp[i][parity] = dp[i-1][parity];
            dp[i][parity^1] = dp[i-1][parity^1];

            // acquire (from same parity or different)
            dp[i][parity] = max(dp[i][parity], max(dp[i-1][parity] + nums[i], dp[i-1][parity^1] + nums[i]-x));

            ans=max(ans, max(dp[i][parity], dp[i][parity^1]));
        }

        return ans;
    }
};
