class Solution {
public:
    long dp[100001][2];
    long helper(int ind, int n, vector<int>& nums, int x, bool ch){
        if(ind == n){
            return 0;
        }
        if(dp[ind][ch] != -1)
            return dp[ind][ch];
        long maxi = 0;         
        bool c = (nums[ind] & 1) ? false : true;   //if nums[ind] is odd then c will be false & if it is even then c will be true
        if(c == ch){
            maxi = max(maxi,nums[ind] + helper(ind+1,n,nums,x,c));
