# Code
```
class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();
        vector<long long> dp={0,0};
        long long ans=0;
        for(int i=n-1;i>=0;i--)
        {
            int temp = nums[i]%2;
            long long t = dp[temp]+nums[i];  // taking parity elements
            long long nt = dp[1-temp]-x+nums[i]; // taking non parity elements
            ans = max(t,nt);  
            dp[temp] = max(dp[temp],ans);  // updating parity dp
        }
        return ans;
    }
};
