class Solution:
    def maxScore(self, nums: List[int], x: int) -> int:
        memo = {}
        def backtrack(i, prev):
            if (i, prev) in memo:
                return memo[(i, prev)]

            if i >= len(nums):
                return 0 
            take1, take2 = 0, 0
            if prev%2 == nums[i]%2:
                take = backtrack(i+1, nums[i]) + nums[i]
            else:
