class Solution {
public:
    long long dp[100005][2];
    vector<int> nums;
    int xs;

    long long solve(int pos, bool prevIsOdd) {
        if (pos == nums.size()) return 0;
        if (dp[pos][prevIsOdd] != -1) return dp[pos][prevIsOdd];
        long long take, notTake;
        if ((prevIsOdd && nums[pos] % 2) || (prevIsOdd == false && nums[pos] % 2 == 0)) {
            take = nums[pos] + solve(pos + 1, prevIsOdd);
        }
        else {
            take = nums[pos] - xs + solve(pos + 1, !prevIsOdd);
        }
        notTake = solve(pos+1, prevIsOdd);
        return dp[pos][prevIsOdd] = max(take, notTake);
    }

    long long maxScore(vector<int>& num, int x) {
        nums = num;
        xs = x;
        memset(dp, -1, sizeof dp);
        return nums[0] + solve(1, nums[0] % 2);
    }
};
```

