class Solution {
public:

    long long help(int i,vector<int>&n,int a,int b,vector<vector<long long>>&dp)
    {
        if(i==n.size()) return 0;
        if(dp[i][b+1]!=-1) return dp[i][b+1];
        long long c=0;
        if(b==-1)
        {
            c= n[i]+help(i+1,n,a,n[i]%2,dp);
        }
        if(b==0)
        {
            if(n[i]%2==0)
            c= max(help(i+1,n,a,b,dp),n[i]+help(i+1,n,a,n[i]%2,dp));
            else
            c= max(help(i+1,n,a,b,dp),n[i]-a+help(i+1,n,a,n[i]%2,dp));
        }
        if(b==1)
        {
            if(n[i]%2==1)
            c= max(help(i+1,n,a,b,dp),n[i]+help(i+1,n,a,n[i]%2,dp));
            else
            c= max(help(i+1,n,a,b,dp),n[i]-a+help(i+1,n,a,n[i]%2,dp));
