class Solution {
public:
    long long solve(int idx,int par,int x ,vector<int> &nums,vector<vector<long long>> &dp){
        int n=nums.size();
        if(idx==n){
            return 0;
        }
        if(dp[idx][par]!=-1){
            return dp[idx][par];
        }
        long long take=0;
        if((nums[idx]%2==0 && par==0) || (nums[idx]%2!=0 && par==1)){
            take+=nums[idx]+solve(idx+1,par,x,nums,dp);
        }
        else{
            take+=nums[idx]-x+solve(idx+1,par^1,x,nums,dp);
        }
        long long nottake=solve(idx+1,par,x,nums,dp);
