class Solution {
    public long maxScore(int[] nums, int x) {
        int n = nums.length;
        // long[][] dp = new long[n][2];
        // for (long[] a : dp)
        //     Arrays.fill(a, -1);
        // int parity = nums[0] % 2;
        // return nums[0] + solve(nums, 1, x, parity, dp);
        // return tabulation(nums, n, x);
        return spaceOptimization(nums, n, x);
    }

    private long solve(int[] nums, int index, int x, int parity, long[][] dp) {
        if (index == nums.length) {
            return 0;
        }
        if (dp[index][parity] != -1)
            return dp[index][parity];
        long notTake = solve(nums, index + 1, x, parity, dp);
        long take = 0;
        if (nums[index] % 2 == parity) {
            take = nums[index] + solve(nums, index + 1, x, parity, dp);
        } else {
            take = nums[index] - x + solve(nums, index + 1, x, nums[index] % 2, dp);
        }
        return dp[index][parity] = Math.max(take, notTake);
    }

    private long tabulation (int[] nums, int n, int x) {
        long[][] dp = new long[n + 1][2];
        dp[n][0] = 0;
        dp[n][1] = 0;

        for (int i = n - 1; i >= 0; i--) {
            for (int j = 0; j <= 1; j++) {
                long notTake = dp[i + 1][j];
                long take = 0;
                if (nums[i] % 2 == j) {
                    take = nums[i] + dp[i + 1][j];
                } else {
                    take = nums[i] - x + dp[i + 1][nums[i] % 2];
                }
                dp[i][j] = Math.max(take, notTake);
            }
        }
        return dp[0][nums[0] % 2];
    }

    private long spaceOptimization (int[] nums, int n, int x) {
        long[] prev = new long[2];

        for (int i = n - 1; i >= 0; i--) {
            long[] curr = new long[2];
            for (int j = 0; j <= 1; j++) {
                long notTake = prev[j];
                long take = 0;
                if (nums[i] % 2 == j) {
                    take = nums[i] + prev[j];
                } else {
                    take = nums[i] - x + prev[nums[i] % 2];
                }
                curr[j] = Math.max(take, notTake);
            }
            prev = curr;
        }
        return prev[nums[0] % 2];
    }
}
