class Solution {
public:
    //for even ->1 
    //for odd ->0
    long long solve(int i,vector<int>& nums, int x,vector<vector<long long>> &dp,int p){

        if(i>=nums.size())return 0;
        if(dp[i][p]!=-1)
            return dp[i][p];

        long long liya =0,nhiliya=0;long long res = 0;
        if(p==1 && nums[i]%2==0){ //for even
            liya = solve(i+1,nums,x,dp,p)+nums[i];
            nhiliya = solve(i+1,nums,x,dp,p);
            res = max(liya,nhiliya);
        }
        else if(p==1 && nums[i]%2!=0){
            liya = solve(i+1,nums,x,dp,0)+nums[i]-x;
            nhiliya = solve(i+1,nums,x,dp,1);
            res = max(liya,nhiliya);
        }else if(p==0 && nums[i]%2==0){
            liya = solve(i+1,nums,x,dp,1)+nums[i]-x;
            nhiliya = solve(i+1,nums,x,dp,p);
            res = max(liya,nhiliya);
        }else if(p==0 && nums[i]%2!=0){
            liya = solve(i+1,nums,x,dp,p)+nums[i];
            nhiliya = solve(i+1,nums,x,dp,p);
            res = max(liya,nhiliya);
        }
        return dp[i][p] = res;

    }

    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();
     vector<vector<long long>> dp(n,vector<long long>(2,-1));
        int p = 0;
        if(nums[0]%2==0)p=1;

        return solve(0,nums,x,dp,p);
    }
};
