class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();  
        vector<vector<long long>> dp(n,vector<long long>(2,-1));
        return f(1,nums[0]%2,x,nums,dp) + nums[0];
    }

    long long f(int idx, int prevParity, int x, vector<int> &nums, vector<vector<long long>> &dp) {
        if(idx >= nums.size()) return 0;

        if(dp[idx][prevParity] != -1) return dp[idx][prevParity];

        long long notPick = f(idx+1,prevParity,x,nums,dp);
        long long pick = 0;

        if(prevParity && nums[idx]%2) pick = 1ll*nums[idx] + f(idx+1,nums[idx]%2,x,nums,dp); // both odd
        else if(!prevParity && nums[idx]%2==0) pick = 1ll*nums[idx] + f(idx+1,nums[idx]%2,x,nums,dp); // both even
        else pick = 1ll*nums[idx] - x + f(idx+1,nums[idx]%2,x,nums,dp); // one odd, one even

        return dp[idx][prevParity] = max(pick,notPick);
    }
};
