class Solution {
public:
    long long solve(int ind,int par,vector<int>&nums, int x, vector<vector<long long>>&dp){
        if(ind==nums.size()) return 0;

        if(dp[ind][par]!=-1) return dp[ind][par];

        long long switching=0;
        if(nums[ind]%2!=par){
            switching = nums[ind] - x + solve(ind+1, nums[ind]%2, nums, x, dp);
        }

        long long not_switching=0;
        if(nums[ind]%2==par){
            not_switching = nums[ind] + solve(ind+1, par, nums, x, dp);
        }

        long long moving_on = 0 + solve(ind+1, par, nums, x, dp);

        return dp[ind][par] = max(moving_on,max(switching,not_switching));

