class Solution:
    def maxScore(self, nums: List[int], x: int) -> int:

        n=len(nums)

        @cache
        def f(ind,p):
            if ind==n:
                return 0

            curr=nums[ind]%2

            if curr==p:
                ans=nums[ind]+f(ind+1,curr)
            else:
                ans=nums[ind]-x+f(ind+1,curr)

            ans=max(ans,f(ind+1,p))
            return ans

        return nums[0] + f(1,nums[0]%2)



