class Solution:
    def maxScore(self, nums: List[int], x: int) -> int:
        n=len(nums)
        @lru_cache(None)
        def f(idx,p):
            if idx>=n:
                return 0
            nopick=f(idx+1,p)
            if p==1 and nums[idx]%2==0:
                pick=nums[idx]+f(idx+1,1)
            elif p==0 and nums[idx]%2!=0:
