class Solution {
public:
    long long ans=-1e9;
    vector<vector<long long>>dp;
    long long f(vector<int>&nums,int x,int ind,int prev,long long score){
        int n=nums.size();
        if(ind==n){
            return 0;
        }
        if(dp[ind][prev]!=-1)return dp[ind][prev];
        long long take=-1e9;
        if(prev!=nums[ind]%2){
            take=nums[ind]-x+f(nums,x,ind+1,nums[ind]%2,score+nums[ind]-x);
        }
        else{
            take=nums[ind]+f(nums,x,ind+1,prev,score+nums[ind]);
        }
        long long notake=f(nums,x,ind+1,prev,score);
        return dp[ind][prev]=max(take,notake);
    }

    long long maxScore(vector<int>& nums, int x) {
        int n=nums.size();
        dp.resize(n,vector<long long>(2,-1));
        return nums[0]+f(nums,x,1,nums[0]%2,nums[0]);
    }
};
