class Solution {
public:
    long long f(int ind, int b, vector<int>& nums, int x, vector<vector<long long>>& dp){
        if(ind == nums.size()) return 0;
        if(dp[ind][b] != -1) return dp[ind][b];
        long long nottake = f(ind+1, b, nums, x, dp);
        long long take = f(ind+1, nums[ind]%2, nums, x, dp) + nums[ind] - ((nums[ind]%2 != b && b != -1) ? x : 0);
        return dp[ind][b] = max(take, nottake);

    }
    long long maxScore(vector<int>& nums, int x) {
        vector<vector<long long>> dp(nums.size(), vector<long long>(2,-1));
        return nums[0] + f(1, nums[0]%2, nums, x, dp);
    }
};
```

