        if(i==nums.size())return 0;
        if(dp[i][preparity]!=-1)return dp[i][preparity];
        long long notpick=f(nums,i+1,x,preparity);
        long long pick=0;
        if(preparity%2==nums[i]%2){
             pick=nums[i]+f(nums,i+1,x,nums[i]%2);

