typedef long long int ll;

class Solution {

public:
    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();

        ll max_odd = 0, max_evn = 0;
        for (int j = n-1; j >= 0; j --) {
            ll nxt_odd, nxt_evn;
            if (nums[j] % 2 == 0) {
                nxt_odd = nums[j] + max_odd - x;
                nxt_evn = nums[j] + max_evn;
            }
            else {
                nxt_odd = nums[j] + max_odd;
                nxt_evn = nums[j] + max_evn - x;
            }

            ll cur = max(nxt_odd, nxt_evn);
            if (nums[j] % 2 == 0) {
                max_evn = max (max_evn, cur);
            }
            else {
                max_odd = max (max_odd, cur);
            }
        }

        return (nums[0] % 2 == 0)? max_evn : max_odd;
    }
};
```

