class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        vector < pair<long long, long long> > dp (nums.size(), {0,0}); //even, odd
        int n = nums.size();
        if (nums[n-1] % 2 == 0){
            dp[n-1].first = nums[n-1];
        }
        else{
            dp[n-1].second = nums[n-1];
        }
        long long deduct = x;
        for (int i=n-2; i>=0; i--){
            long long temp = nums[i];
            if (nums[i] % 2 == 0){ 
