class Solution {
public:
    long long maxScore(vector<int>& a, int x) {
        const int n = (int)a.size();
        vector<array<int, 2>> nxt(n);
        int e = n, o = n;
        for (int i = n - 1; i >= 0; i--) {
            nxt[i][0] = e, nxt[i][1] = o;
            if (a[i] & 1) o = i;
            else e = i;
        }
        vector<int64_t> dp(n + 1, -1e9);
        for (int i = n; i >= 0; i--) {
            if (i == n) {
                dp[i] = 0;
            } else {
                int p = a[i] & 1;
                dp[i] = max(dp[nxt[i][p]], dp[nxt[i][1 - p]] - x) + a[i];
            }
        }
        return dp.front();
    }
};
```

