class Solution {
    long long solve(int i, int prevParity, vector<int>& nums, int x, vector<vector<long long>>& dp)
    {
        if(i == nums.size())
         return 0;

        if(dp[i][prevParity] != -1) //memoization step
         return dp[i][prevParity];

        int currParity = nums[i]&1;
        //if prev choosen number and current number have same parity
