    long long maxScore(vector<int>& nums, int x) {

        int n = nums.size();
        int odd = -1;
        int even = -1;
        vector<long long> scores(n,0);
        for(int i =(n-1);i>=0;i--)
        {
            if((odd==-1)&(even==-1))
            {
                if(nums[i]%2==0)
                    even =i;
                else
                    odd =i;
                scores[i] = nums[i];
                continue;
            }
            if(odd==-1)
            {
                long long y = nums[i] +scores[even];
                int a = nums[i]%2;
                int b = nums[even]%2;
                if(a!=b)
                    y = y-x;
                if(y>nums[i])
                    scores[i] =y;
                else
                    scores[i] = nums[i];
                if(a==1)
                    odd =i;
                else
                    even =i;
                continue;
            }
            if(even==-1)
            {
                long long y = nums[i] +scores[odd];
                int a = nums[i]%2;
                int b = nums[odd]%2;
                if(a!=b)
                    y = y-x;
                if(y>nums[i])
                    scores[i] =y;
                else
                    scores[i] = nums[i];
                if(a==1)
                    odd =i;
                else
                    even =i;
                continue;
            }
            long long y = nums[i] +scores[odd];
            int a = nums[i]%2;
            int b = nums[odd]%2;
            if(a!=b)
                y = y-x;
            long long z = nums[i]+scores[even];
            int c = nums[even]%2;
            if(a!=c)
                z = z-x;
            z =  max(z,y);
            if(z>nums[i])
                scores[i]=z;
            else
                scores[i] = nums[i];
            if(a==1)
                odd =i;
            else
                even =i;

        }
        // for(int i =0;i<n;i++)
        //     cout<<scores[i]<<" ";
        return scores[0];
    }
};
