class Solution {
public: 
typedef long long ll;
    ll helper(int pos, int flag, vector<int>& v, int x, vector<vector<ll>>& dp)
    {
        int n=v.size();
        if(pos==n) return 0;
        if(dp[pos][flag]!=-1) return dp[pos][flag];
        ll notPick = helper(pos+1,flag,v,x,dp);
        ll pick = 0;
        if(v[pos]%2==flag) pick=v[pos] + helper(pos+1,flag,v,x,dp);
        else pick = v[pos] + helper(pos+1,v[pos]%2,v,x,dp) - x;
        return dp[pos][flag] = max(pick,notPick);
    }
    long long maxScore(vector<int>& nums, int x) {
        vector<vector<ll>> dp(nums.size(),vector<ll>(2,-1));
        return nums[0] + helper(1,nums[0]%2,nums,x,dp);
    }
};
