class Solution {
    public long maxScore(int[] nums, int x) {
        long currentOddMax = (nums[0] % 2 != 0)? nums[0] : -1000000, currentEvenMax = (nums[0] % 2 == 0)? nums[0] : -1000000;
        long finalMax = nums[0];
        for(int i=1; i<nums.length; i++) {
            if(nums[i] % 2 == 0) {
                long currentMax = Math.max(currentOddMax - x, currentEvenMax);
                currentEvenMax = nums[i] + currentMax;
            }
            else {
                long currentMax = Math.max(currentEvenMax - x, currentOddMax);
                currentOddMax = nums[i] + currentMax;
            }
            finalMax = Math.max(currentEvenMax, currentOddMax);
        }
        return finalMax;

