class Solution:
    def maxScore(self, nums: List[int], x: int) -> int:
        def recurse(i):
            if i >= len(arr):
                return 0
            if dp[i] > 0:
                return dp[i]
            dp[i] = arr[i] + max(recurse(i+1) - x, recurse(i+2))
            return dp[i]

        arr = [nums[0]]
        parity = nums[0] % 2
        for i in range(1, len(nums)):
            if nums[i] % 2 == parity:
                arr[-1] += nums[i]
            else:
                parity = nums[i] % 2
                arr.append(nums[i])

        dp = [0 for i in range(len(arr))]
        return recurse(0)
