# Code
```
class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        int n=nums.size();
        vector<vector<long long>> dp(n,vector<long long>(2,LONG_MIN));
        function<long long(int,int)> dfs=[&](int ind,int even){
            if(ind==n) return 0LL;
            if(dp[ind][even]!=LONG_MIN) return dp[ind][even];
            long long res=dfs(ind+1,even);
            if(nums[ind]%2==0&&even==0) {
                res=max(res,nums[ind]-x+dfs(ind+1,1));
            }
            else if(nums[ind]%2==0&&even==1) {
                res=max(res,nums[ind]+dfs(ind+1,1));
            }
            else if(nums[ind]%2==1&&even==0) {
                res=max(res,nums[ind]+dfs(ind+1,0));
            }
            else if(nums[ind]%2==1&&even==1) {
                res=max(res,nums[ind]-x+dfs(ind+1,0));
            }
            return dp[ind][even]=res;
        };
        return nums[0]+dfs(1,nums[0]%2==0);
    }
};
```

