# Code
```
class Solution {
public:
    long long f(long long int n,long long prev,long long int x,vector<int>& arr,vector<vector<long long>> &dp){
        if(n==0){
            if(prev==2 || arr[n]%2 == prev) return dp[n][prev] = arr[n];
            else return dp[n][prev] = arr[n] - x;
        }

        if(dp[n][prev] != -1) return dp[n][prev];

        long long nt = f(n-1,prev,x,arr,dp);
        long long t=0;
        if(prev == 2 || arr[n]%2 == prev){
            t = arr[n] + f(n-1,arr[n]%2,x,arr,dp);
        }else{
            t = arr[n] - x + f(n-1,arr[n]%2,x,arr,dp);
        } 
        return dp[n][prev] = max(nt,t);
    }
    long long maxScore(vector<int>& arr, int x) {
        long long int n = arr.size();
