  def maxScore(self, nums: List[int], x: int) -> int:
    max_odd, max_even = (-float('inf'), 0) if nums[0] % 2 == 0 else (0, -float('inf'))
    for v in nums:
      if v % 2:
        max_odd = max(max_odd + v, max_even + v - x)
      else:
        max_even = max(max_even + v, max_odd + v - x)

    return max(max_odd, max_even)
