class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();
        vector<long long> curr(2);
        vector<long long> next(2);

        for(int i = n - 1; i >= 1; i--) {
            for(int isPrevOdd = 0; isPrevOdd <= 1; isPrevOdd++) {
                long long op1 = next[isPrevOdd];
                long long op2 = nums[i] + next[nums[i] & 1];

                if(isPrevOdd && (nums[i] & 1) == 0) op2 -= x;
                if(!isPrevOdd && (nums[i] & 1)) op2 -= x;

                curr[isPrevOdd] = max(op1, op2);
            }
            next = curr;
        }
        return nums[0] + curr[nums[0] & 1];
    }
};
```

